    var ret = {};
    for (var key in obj) {
        if (obj.hasOwnProperty(key)) {
            if (typeof obj[key] === 'object') {
                ret[key] = deepCopy(obj[key]);
            } else {
                ret[key] = obj[key];
            }        }

}
return ret;
